import main.scala.com.github.nielsenbe.sparkwikiparser.wikipedia._

// Settings and configuration
val config = WkpParserConfiguration(
    parseText = true, 
    parseTemplates = true, 
    parseLinks = true, 
    parseTags = true, 
    parseTables = true, 
    parseRefTags = false)

// Update file location info
// S3 = S3://<bucketName>/<folders>
// Azure = wasb://<containerName>@<storageAccountName>.blob.core.windows.net/<folders>
// HDFS = hdfs://<folders>
val folder = "S3://bnielsenemr/"
val destFolder = "wkpDb"
val sourceFile = "enwiki-20171020-pages-articles.xml.bz2"

// Concat values
val source = folder + sourceFile
val destDB = folder + destFolder + "/"

val parsed = ds.mapPartitions(part => {
    for(page <- part) yield WkpParser.parseWiki(page, config)
})

val items = parsed.cache
val pageCount = items.count
